import React from "react";
import {  View, StyleSheet ,SafeAreaView ,StatusBar , Platform, Image } from "react-native";
import ButtonCampoo from "../components/ButtonCampoo";
import InputCampoo from "../components/InputCampoo";
import SecondaryButtonCampoo from "../components/SecondaryButtonCampoo";
import LabelCampoo from "../components/LabelCampoo";
import InputCampooSignup from  "../components/InputCampooSignup"

export default class ScreenLoginPage extends React.Component {
    render(){
       return(
        

          <SafeAreaView style={styles.container} >  

            <Image style={{width:218.83, height:219.17, left:-80.42, top :40, zIndex:-6000, }} source={require("../../assets/images/baloostudent.png")}/>
            <Image style={{width:518.23, height:529.49, left:135,  top :100,  }} source={require("../../assets/images/ponpondroite.png")}/>
            <Image style={{width:100, height:100, left:275, top:-750,  }} source={require("../../assets/images/logoCampooAncien.png")}/>
       

         
           
            <View style={styles.loginview}>    
                  
                  <View style={styles.pseudo}>

                     <LabelCampoo style={styles.labelPseudo}>Pseudo</LabelCampoo>
                     <InputCampooSignup name="Pseudo" style={styles.InputPseudo}/>

                  </View>

                 <View>

                    <LabelCampoo style={styles.labelMotDePasse}> Mot de passe</LabelCampoo>
                    <InputCampooSignup name="Mot de passe" secure={true} style={styles.InputMotDePasse}/>
                    <ButtonCampoo style={styles.button}>Connexion</ButtonCampoo>

                 </View>

                 

               <View>
                 
                  <SecondaryButtonCampoo style={styles.secondaryButton}>Mot de passe oubli√©</SecondaryButtonCampoo>
                 
                  <SecondaryButtonCampoo style={styles.secondaryButton}>S'incrire</SecondaryButtonCampoo>

               </View>

             
            </View>

          </SafeAreaView>
             
       );
    }
}

const styles = StyleSheet.create({

   container : {
      
      flex : 1,
      paddingTop : Platform.OS === "android" ? StatusBar.currentHeight: 1,
      flexDirection: 'column',
      alignContent:'center',
     
      
   },

   pseudo:{
     
      marginBottom:20,
   },

   labelPseudo:{
      marginBottom:-19,
   },
   labelMotDePasse:{
 
      paddingBottom:0,
      marginBottom:-19,

   },
 
  loginview: {
   
     alignSelf:'center',
     top:-620,
     width : 300,
     height: 270,  


   },
   InputPseudo:{
      
      marginTop:0,
      paddingTop:0,
      paddingBottom:0,
      
  },


   InputMotDePasse:{
      paddingTop:0,
       marginTop:0,
       
   },
 
   connexionText:{

      textAlign :'center',
      marginVertical:15,
      fontSize:16,
      color:'white',

   },
   register:{ 

      alignSelf:'center',
      marginVertical:-20,
      color:'#62BBC5',
      fontSize:15,

   },
   button:{ 

      marginTop:30,
      alignSelf :'center'

    },

   
    secondaryButton: {

      alignSelf:'center',
      marginTop:10

    }

});
